mapper.select:
select m.mes_id, m.mes_itemid, i.itemname as mes_itemname, m.mes_levuserid, u.nickname as mes_levusername, m.mes_content, m.mes_status, m.createtime, m.audittime from message m left join item i on m.mes_itemid = i.itemid join user_normal u on m.mes_levuserid = u.usernormal_id where m.mes_id!=0
and m.mes_itemid = #{Itemid}
and m.mes_levuserid =#{LevUserid}
and m.mes_content = #{Content}
and m.mes_statu s= #{Status}
and m.createime >=date_format(#{StartCreateTime},'%Y-%m-%d')
and m.createime <=date_format(#{EndCreateTime},'%Y-%m-%d')
order by m.createtime desc limit #{beginIndex},#{pageSize}

select
count(*)
from message m left join item i on m.mes_itemid = i.itemid join user_normal u on m.mes_levuserid = u.usernormal_id
where m.mes_id!=0

	and m.mes_itemid = #{Itemid}


	and m.mes_levuserid =#{LevUserid}


	and m.mes_content =#{Content}


	and m.mes_status =#{Status}


	and m.createime >=date_format(#{StartCreateTime},'%Y-%m-%d')


	and m.createime <=date_format(#{EndCreateTime},'%Y-%m-%d')

mapper.delete:
		delete from message
		where mes_itemid in (
		  
        	#{item}
    	
		)
